$DefaultGroup = Get-ADGroup -Filter {name -like "Domain Admins"}


$iterator = 0
$ErrorActionPreference = "continue"

    while ($iterator -le 1000) {$iterator ++
        $UserFname = Get-Random $fnames
        $UserLname = Get-Random $lnames
        $department = Get-Random $office
        $logonName = $UserFname+'.'+$UserLname
        $displayname = $UserLname+','+' '+$UserFname
        $email = $logonName+"@state.gov"
    
